import * as d3 from 'd3';

export function drawMap(height, width){

function legend({
    color,
    title,
    tickSize = 6,
    width = 800,
    height = 50 + tickSize,
    marginTop = 18,
    marginRight = 0,
    marginBottom = 16 + tickSize,
    marginLeft = 0,
    ticks = width / 64,
    tickFormat,
    tickValues
} = {}) {  

    const svg = d3.create("svg")
        .attr("width", width)
        .attr("height", height)
        .attr("viewBox", [0, 0, width, height])
        .style("overflow", "visible")
        .style("display", "block");

    let x;

    // Continuous
    if (color.interpolator) {
        x = Object.assign(color.copy()
            .interpolator(d3.interpolateRound(marginLeft, width - marginRight)),
            { range() { return [marginLeft, width - marginRight]; } });

        svg.append("image")
            .attr("x", marginLeft)
            .attr("y", marginTop)
            .attr("width", width - marginLeft - marginRight)
            .attr("height", height - marginTop - marginBottom)
            .attr("preserveAspectRatio", "none")
            .attr("xlink:href", ramp(color.interpolator()).toDataURL());

        //scaleSequentialQuantile doesn’t implement ticks or tickFormat.
        if (!x.ticks) {
            if (tickValues === undefined) {
                const n = Math.round(ticks + 1);
                tickValues = d3.range(n).map(i => d3.quantile(color.domain(), i / (n - 1)));
            }
            if (typeof tickFormat !== "function") {
                tickFormat = d3.format(tickFormat === undefined ? ",f" : tickFormat);
            }
        }
    }

    //discrete
    else if (color.invertExtent) {
        const thresholds
            = color.thresholds ? color.thresholds() // scaleQuantize
                : color.quantiles ? color.quantiles() // scaleQuantile
                    : color.domain(); // scaleThreshold

        const thresholdFormat
            = tickFormat === undefined ? d => d
                : typeof tickFormat === "string" ? d3.format(tickFormat)
                    : tickFormat;

        x = d3.scaleLinear()
            .domain([-1, color.range().length - 1])
            .rangeRound([marginLeft, width - marginRight]);

        svg.append("g")
            .selectAll("rect")
            .data(color.range())
            .join("rect")
            .attr("x", (d, i) => x(i - 1))
            .attr("y", marginTop)
            .attr("width", (d, i) => x(i) - x(i - 1))
            .attr("height", height - marginTop - marginBottom)
            .attr("fill", d => d);

        tickValues = d3.range(thresholds.length);
        tickFormat = i => thresholdFormat(thresholds[i], i);
    }

    svg.append("g")
        .attr("transform", `translate(0, ${height - marginBottom})`)
        .call(d3.axisBottom(x)
            .ticks(ticks, typeof tickFormat === "string" ? tickFormat : undefined)
            .tickFormat(typeof tickFormat === "function" ? tickFormat : undefined)
            .tickSize(tickSize)
            .tickValues(tickValues))
        .call(g => g.selectAll(".tick line").attr("y1", marginTop + marginBottom - height))
        .call(g => g.select(".domain").remove())
        .call(g => g.append("text")
            .attr("y", marginTop + marginBottom - height - 6)
            .attr("fill", "currentColor")
            .attr("text-anchor", "start")
            .attr("font-weight", "bold")
            .text(title));

    return svg.node();
}

function ramp(color, n = 256) {
    const canvas = DOM.canvas(n, 1);
    const context = canvas.getContext("2d");
    for (let i = 0; i < n; ++i) {
        context.fillStyle = color(i / (n - 1));
        context.fillRect(i, 0, 1, 1);
    }
    return canvas;
}

/////////////////////////////////////////////////////////////

var promises = [];

promises.push(d3.json("data/coh_area_rw.json"));
promises.push(d3.json("data/data.json"));

Promise.all(promises).then(function (values) {  
    var la_map = values[0];
    var canc_data = values[1];
    var data = values[2]

    color = d3.scaleQuantize([0, 36], d3.schemeReds[9])  
    console.log(canc_data)

    canc_data = Object.assign(new Map(canc_data.slice(1).map(([zip_code,zip_canc_deaths_10k]) => [zip_code, +zip_canc_deaths_10k])));  

    canc_data.title = "2019 cancer deaths per 10k";

    console.log(data)
    console.log(canc_data)
    var svg = d3.select('#chor1'),
        width = +svg.attr('width'),
        height = +svg.attr('height');
    projection = d3.geoMercator()
        .fitSize([width, height], la_map);
    path = d3.geoPath()
        .projection(projection);

    svg.append("g")
        .attr("transform", "translate(610,20)")
        .append(() => legend({ color, title: canc_data.title, width: 200 }));

    svg.append('g')
        .attr('class', 'zipcodes')
        .selectAll("path")
        .data(la_map.features)
        .enter()
        .append('path')
        .attr('d', path)
        .attr("fill", d => color(canc_data.get(d.properties.zcta))) 
        .on('mouseover', function (d) {
            d3.select('#chor_info1')
                .text('Zip code: ' +  this.__data__.properties.zcta +  ' - Total population: ' +  this.__data__.properties.tot_pop + ' - Median age: ' +  this.__data__.properties.age_median + ' years');               
            })
        ;


    coh_hosp = [-117.97265500193778, 34.131232240226815];

    marker = projection(coh_hosp);
    svg.append('circle')
        .attr('cx', marker[0])
        .attr('cy', marker[1])
        .attr('fill', 'pink')
        .style("stroke", 'darkred')
        .style("stroke-width", 0.8)
        .attr('r', 8);

});

}
